// src/components/HeroSection.js
import React, { useEffect } from 'react';
import { gsap } from 'gsap';
import { motion } from 'framer-motion';
import Lottie from 'lottie-react';
import particleAnimation from '/animations/particleAnimation.json';

const HeroSection = () => {
    const styles = {
        hero: {
            position: 'relative',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            padding: '4rem 2rem',
            color: 'white',
            textAlign: 'center',
            overflow: 'hidden',
            height: '100vh',
            background: 'linear-gradient(120deg, #1c1c1c, #333)',
        },
        overlay: {
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            zIndex: 1,
            backgroundColor: 'rgba(0, 0, 0, 0.3)',
            pointerEvents: 'none',
        },
        content: {
            position: 'relative',
            zIndex: 2,
            maxWidth: '800px'
        },
        title: {
            fontSize: '3rem',
            marginBottom: '0.5rem',
            color: '#ffd700',
            textShadow: '0px 0px 10px rgba(255,215,0,0.5)'
        },
        tagline: {
            fontSize: '1.3rem',
            color: '#ffd700',
        },
        description: {
            margin: '1rem 0 2rem',
            fontSize: '1.1rem',
            lineHeight: 1.6,
        },
        buttonContainer: {
            display: 'flex',
            justifyContent: 'center',
            gap: '1rem'
        },
        primaryButton: {
            padding: '0.75rem 1.5rem',
            borderRadius: '5px',
            textDecoration: 'none',
            fontWeight: 'bold',
            backgroundColor: '#ffd700',
            color: '#333',
            transition: 'transform 0.3s, box-shadow 0.3s',
        },
        secondaryButton: {
            padding: '0.75rem 1.5rem',
            borderRadius: '5px',
            textDecoration: 'none',
            fontWeight: 'bold',
            backgroundColor: '#333',
            color: '#ffd700',
            border: '1px solid #ffd700',
            transition: 'transform 0.3s, box-shadow 0.3s'
        }
    };

    useEffect(() => {
        gsap.fromTo('.hero-title', { opacity: 0, y: -50 }, { opacity: 1, y: 0, duration: 1 });
        gsap.fromTo('.hero-button', { opacity: 0, scale: 0.9 }, { opacity: 1, scale: 1, duration: 0.5, stagger: 0.3 });
    }, []);

    return (
        <section style={styles.hero}>
            {/* Animated Gradient Background */}
            <motion.div
                style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    zIndex: -2,
                    background: 'linear-gradient(120deg, #1c1c1c, #282c34, #333)',
                    filter: 'blur(5px)',
                }}
                animate={{ opacity: [0.7, 1, 0.7] }}
                transition={{ duration: 15, repeat: Infinity, repeatType: 'mirror' }}
            />

            {/* Particle Animation using Lottie */}
            <Lottie
                animationData={particleAnimation}
                style={{
                    position: 'absolute',
                    top: 0,
                    left: 0,
                    width: '100%',
                    height: '100%',
                    zIndex: -1,
                }}
                loop
                autoplay
            />

            <div style={styles.overlay}></div>
            <div style={styles.content}>
                <h1 className="hero-title" style={styles.title}>Hello, I'm Udhaya Kumar A</h1>
                <p style={styles.tagline}>Aspiring Public Speaker, AI Enthusiast, and Technology Innovator</p>
                <p style={styles.description}>
                    A Computer Science Engineering student passionate about harnessing the power of AI and technology to solve real-world challenges.
                </p>
                <div style={styles.buttonContainer}>
                    <a href="#projects" className="hero-button" style={styles.primaryButton}>View Projects</a>
                    <a href="/path/to/resume.pdf" target="_blank" className="hero-button" style={styles.secondaryButton}>Download Resume</a>
                </div>
            </div>
        </section>
    );
};

export default HeroSection;
