// src/components/LoadingSpinner.js
import React from 'react';
import styled, { keyframes } from 'styled-components';

// Neon glow effect for the EKG line
const neonGlow = keyframes`
  0%, 100% {
    filter: drop-shadow(0 0 5px #00e6e6) drop-shadow(0 0 10px #00e6e6);
  }
  50% {
    filter: drop-shadow(0 0 20px #00e6e6) drop-shadow(0 0 30px #00e6e6);
  }
`;

// Container for the loading spinner
const SpinnerContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  width: 100vw;
  background-color: #1a1a1a;
  text-align: center;
`;

// Styled SVG for the EKG line animation
const EKGLineSVG = styled.svg`
  width: 300px;  // Reduced width for better fit and faster animation
  height: 120px; // Adjusted height to match the new width
  overflow: visible;
  animation: ${neonGlow} 3s infinite; // Neon glow effect
  path {
    fill: none;
    stroke: #00e6e6;
    stroke-width: 2;
    stroke-linecap: round;
    stroke-dasharray: 600;  // Adjusted dash length
    stroke-dashoffset: 600;
    animation: dash 1.5s cubic-bezier(0.4, 0, 0.2, 1) infinite;  // Increased speed for a quicker pulse effect
  }

  // Keyframes for the SVG stroke dash offset animation
  @keyframes dash {
    to {
      stroke-dashoffset: 0;
    }
  }
`;

// Loading text with neon glow effect
const LoadingText = styled.p`
  color: #00e6e6;
  font-size: 1.5rem;
  font-weight: bold;
  margin-top: 20px;  // Slight margin to separate text from animation
  text-shadow: 0 0 5px #00e6e6, 0 0 10px #00e6e6;
  animation: neonGlowText 3s infinite alternate;
`;

// Neon glow animation for the text
const neonGlowText = keyframes`
  0% {
    text-shadow: 0 0 5px #00e6e6, 0 0 10px #00e6e6;
  }
  50% {
    text-shadow: 0 0 20px #00e6e6, 0 0 30px #00e6e6;
  }
  100% {
    text-shadow: 0 0 5px #00e6e6, 0 0 10px #00e6e6;
  }
`;

// LoadingSpinner component
const LoadingSpinner = () => (
  <SpinnerContainer>
    <EKGLineSVG viewBox="0 0 300 120">
      <path d="M0,60 L50,60 L60,20 L80,60 L120,60 L140,100 L160,60 L200,60" />
    </EKGLineSVG>
    <LoadingText>Loading...</LoadingText>
  </SpinnerContainer>
);

export default LoadingSpinner;
