// src/components/MicroInteractions.js
import React, { useState, useEffect } from 'react';
import { gsap } from 'gsap';

const MicroInteractions = () => {
    const [isGameActive, setIsGameActive] = useState(false);
    const [score, setScore] = useState(0);
    const [timeLeft, setTimeLeft] = useState(10); // 10 seconds for the game
    const [circlePosition, setCirclePosition] = useState({ top: '50%', left: '50%' });

    useEffect(() => {
        let timer;
        if (isGameActive && timeLeft > 0) {
            timer = setInterval(() => {
                setTimeLeft(prev => prev - 1);
            }, 1000);
        } else if (timeLeft === 0) {
            endGame();
        }
        return () => clearInterval(timer);
    }, [isGameActive, timeLeft]);

    const startGame = () => {
        setIsGameActive(true);
        setScore(0);
        setTimeLeft(10);
        moveCircle();
    };

    const endGame = () => {
        setIsGameActive(false);
        alert(`Game Over! Your score is: ${score}`);
    };

    const moveCircle = () => {
        const x = Math.random() * (window.innerWidth - 100); // Subtracting circle size
        const y = Math.random() * (window.innerHeight - 100); // Subtracting circle size
        setCirclePosition({ top: `${y}px`, left: `${x}px` });
        
        gsap.to('.neon-circle', {
            scale: 1.2,
            duration: 0.5,
            yoyo: true,
            repeat: -1,
            ease: "power1.inOut"
        });
    };

    const handleCircleClick = () => {
        if (isGameActive) {
            setScore(prevScore => prevScore + 1);
            moveCircle(); // Move the circle after click
        }
    };

    return (
        <section style={styles.container}>
            <h2 style={styles.title}>Neon Click Game</h2>
            <p style={styles.score}>Score: {score}</p>
            <p style={styles.timer}>Time Left: {timeLeft}s</p>

            {isGameActive ? (
                <div 
                    className="neon-circle" 
                    style={{ ...styles.circle, ...circlePosition }} 
                    onClick={handleCircleClick}
                />
            ) : (
                <button style={styles.startButton} onClick={startGame}>
                    Start Game
                </button>
            )}
        </section>
    );
};

const styles = {
    container: {
        padding: '2rem',
        textAlign: 'center',
        backgroundColor: '#1a1a1a',
        height: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
    },
    title: {
        fontSize: '2rem',
        color: '#00ffcc',
        textShadow: '0 0 20px #00ffcc, 0 0 30px #00ffcc',
    },
    score: {
        fontSize: '1.5rem',
        color: '#fff',
    },
    timer: {
        fontSize: '1.5rem',
        color: '#fff',
    },
    startButton: {
        padding: '1rem 2rem',
        fontSize: '1rem',
        backgroundColor: '#00ffcc',
        borderRadius: '6px',
        border: 'none',
        cursor: 'pointer',
        color: '#000',
        transition: 'background-color 0.3s ease-in-out',
    },
    circle: {
        position: 'absolute',
        width: '100px',
        height: '100px',
        borderRadius: '50%',
        backgroundColor: '#ff007f', // Neon pink
        boxShadow: '0 0 20px #ff007f, 0 0 30px #ff007f', // Neon glow effect
    }
};

export default MicroInteractions;
