// src/components/LoadingSpinner.js
import React from 'react';
import styled, { keyframes } from 'styled-components';

const pulse = keyframes`
    0%, 100% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.1);
    }
`;

const neonGlow = keyframes`
    0% {
        text-shadow: 0 0 5px #00e6e6, 0 0 10px #00e6e6, 0 0 15px #00e6e6, 0 0 20px #00e6e6;
    }
    50% {
        text-shadow: 0 0 10px #00e6e6, 0 0 20px #00e6e6, 0 0 30px #00e6e6, 0 0 40px #00e6e6;
    }
    100% {
        text-shadow: 0 0 5px #00e6e6, 0 0 10px #00e6e6, 0 0 15px #00e6e6, 0 0 20px #00e6e6;
    }
`;

const SpinnerContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100vh; /* Full height of the viewport */
    background-color: #1a1a1a; /* Dark background for neon effect */
    color: #333; /* Text color */
`;

const Heart = styled.div`
    position: relative;
    width: 100px;
    height: 100px;
    background-color: #e63946;
    transform: rotate(-45deg);
    animation: ${pulse} 1s infinite;
    box-shadow: 0 0 20px #00e6e6, 0 0 30px #00e6e6, 0 0 40px #00e6e6; /* Neon glow effect */

    &::before,
    &::after {
        content: '';
        position: absolute;
        width: 100px;
        height: 100px;
        background-color: #e63946;
        border-radius: 50%;
    }

    &::before {
        top: -50px;
        left: 0;
    }

    &::after {
        left: 50px;
        top: 0;
    }
`;

const PulseEffect = styled.div`
    position: absolute;
    top: 50%;
    left: 50%;
    width: 60px;
    height: 60px;
    background-color: rgba(230, 57, 70, 0.5);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    animation: ${pulse} 1s infinite;
    box-shadow: 0 0 20px #00e6e6, 0 0 30px #00e6e6; /* Neon glow effect */
`;

const LoadingSpinner = () => {
    return (
        <SpinnerContainer>
            <Heart>
                <PulseEffect />
            </Heart>
            <p style={{ animation: `${neonGlow} 1.5s infinite` }}>Loading...</p>
        </SpinnerContainer>
    );
};

export default LoadingSpinner;
