// src/pages/ProjectsPage.js
import React from 'react';
import ProjectList from '../components/ProjectList';
import ScrollAnimations from '../components/ScrollAnimations';
import { gsap } from 'gsap';

const ProjectsPage = () => {
    const styles = {
        pageContainer: {
            padding: '2rem',
            backgroundColor: '#1e1e1e',
            fontFamily: 'Arial, sans-serif',
            color: '#f1f1f1',
            minHeight: '100vh', // Ensure full height
        },
        title: {
            fontSize: '3rem',
            textAlign: 'center',
            color: '#ffd700',
            textShadow: '2px 2px 6px rgba(0, 0, 0, 0.3)',
            marginBottom: '2rem'
        },
        featuredSection: {
            marginTop: '3rem',
            padding: '2rem',
            backgroundColor: '#333',
            borderRadius: '10px',
            boxShadow: '0 8px 16px rgba(0, 0, 0, 0.3)',
            textAlign: 'center'
        },
        featuredTitle: {
            fontSize: '2.5rem',
            color: '#ffd700',
            marginBottom: '0.8rem'
        },
        featuredDescription: {
            fontSize: '1.2rem',
            color: '#ddd',
            marginBottom: '2rem'
        },
        projectGrid: {
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
            gap: '1.5rem'
        },
        featuredProjectCard: {
            backgroundColor: '#444',
            borderRadius: '8px',
            padding: '1rem',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
            transition: 'transform 0.3s, box-shadow 0.3s'
        }
    };

    React.useEffect(() => {
        gsap.fromTo('.featuredSection', { opacity: 0, y: 50 }, { opacity: 1, y: 0, duration: 1, delay: 0.5 });
    }, []);

    return (
        <div style={styles.pageContainer}>
            <h1 style={styles.title}>My Projects</h1>
            <ScrollAnimations />
            
            <ProjectList />
            
            <section style={styles.featuredSection} className="featuredSection">
                <h2 style={styles.featuredTitle}>Featured Projects</h2>
                <p style={styles.featuredDescription}>Here are some of the standout projects Iâ€™ve worked on:</p>
                <div style={styles.projectGrid}>
                    {/* Featured project highlights */}
                    <div style={styles.featuredProjectCard}>
                        <h3>AI-Based Atmospheric Model</h3>
                        <p>Integrates real-time satellite data for tracking pollutant dispersion.</p>
                        <a href="#" style={{ color:'#ffd700', textDecoration:'underline' }}>View Project</a>
                    </div>
                    <div style={styles.featuredProjectCard}>
                        <h3>Multi-Modal Gunshot Detection System</h3>
                        <p>Advanced detection system using AI technologies.</p>
                        <a href="#" style={{ color:'#ffd700', textDecoration:'underline' }}>View Project</a>
                    </div>
                    <div style={styles.featuredProjectCard}>
                        <h3>Cognitive Function Simulation</h3>
                        <p>Mimics cognitive functions using neural networks.</p>
                        <a href="#" style={{ color:'#ffd700', textDecoration:'underline' }}>View Project</a>
                    </div>
                </div>
            </section>
        </div>
    );
};

export default ProjectsPage;
