// src/pages/HomePage.js
import React, { useEffect } from 'react';
import { gsap } from 'gsap';
import HeroSection from '../components/HeroSection';
import AboutMe from '../components/AboutMe';
import Skills from '../components/Skills';
import MicroInteractions from '../components/MicroInteractions';
import KineticTypography from '../components/KineticTypography';

const HomePage = () => {
    const styles = {
        homePage: {
            fontFamily: 'Arial, sans-serif',
            color: 'var(--text-color)',
            background: 'linear-gradient(135deg, rgba(0,0,0,0.9), rgba(0,0,0,0.7)), url("/background.jpg") center/cover no-repeat',
            padding: '2rem'
        },
        introduction: {
            textAlign: 'center',
            margin: '4rem 0',
            padding: '3rem 2rem',
            background: 'rgba(255, 255, 255, 0.85)',
            boxShadow: '0 8px 12px rgba(0, 0, 0, 0.2)',
            borderRadius: '15px',
            animation: 'fadeIn 1.5s ease-in-out'
        },
        title: {
            fontSize: '3rem',
            color: 'var(--accent-color)',
            marginBottom: '1rem',
            animation: 'typing 2s steps(40, end), blink 0.5s step-end infinite alternate'
        },
        description: {
            fontSize: '1.2rem',
            lineHeight: 1.6,
            maxWidth: '800px',
            margin: '0 auto',
            color: 'var(--text-color)',
            opacity: 0,
            transform: 'translateY(20px)'
        }
    };

    useEffect(() => {
        gsap.fromTo('.intro-description', { opacity: 0, y: 20 }, { opacity: 1, y: 0, duration: 1, delay: 1 });
    }, []);

    return (
        <div style={styles.homePage}>
            <HeroSection />
            <section style={styles.introduction}>
                <h2 style={styles.title} className="typewriter-text">Welcome to My Portfolio!</h2>
                <p style={styles.description} className="intro-description">
                    I'm Udhaya Kumar A, a passionate Computer Science Engineer on a journey to innovate and create impactful solutions through technology.
                </p>
            </section>
            <Skills />
            <MicroInteractions />
            <KineticTypography />
            <AboutMe />
        </div>
    );
};

export default HomePage;
