// src/components/LoadingSpinner.js
import React from 'react';
import styled, { keyframes } from 'styled-components';

// Neon glow effect for the EKG line
const neonGlow = keyframes`
  0%, 100% {
    filter: drop-shadow(0 0 5px #00e6e6) drop-shadow(0 0 10px #00e6e6);
  }
  50% {
    filter: drop-shadow(0 0 20px #00e6e6) drop-shadow(0 0 30px #00e6e6);
  }
`;

// Container for the loading spinner
const SpinnerContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #1a1a1a;
`;

// Styled SVG for the EKG line animation
const EKGLineSVG = styled.svg`
  width: 200px;
  height: 100px;
  overflow: visible;
  animation: ${neonGlow} 2s infinite;
  path {
    fill: none;
    stroke: #00e6e6;
    stroke-width: 2;
    stroke-linecap: round;
    stroke-dasharray: 250;
    stroke-dashoffset: 250;
    animation: dash 2s linear infinite;
  }

  // Keyframes for the SVG stroke dash offset animation
  @keyframes dash {
    to {
      stroke-dashoffset: 0;
    }
  }
`;

// Loading text with neon glow effect
const LoadingText = styled.p`
  color: #00e6e6;
  font-size: 1.2rem;
  font-weight: bold;
  margin-top: 20px;
  text-shadow: 0 0 5px #00e6e6, 0 0 10px #00e6e6;
`;

// LoadingSpinner component
const LoadingSpinner = () => (
  <SpinnerContainer>
    <EKGLineSVG viewBox="0 0 100 50">
      <path d="M0,25 L10,25 L15,10 L20,25 L30,25 L35,40 L40,25 L50,25" />
    </EKGLineSVG>
    <LoadingText>Loading...</LoadingText>
  </SpinnerContainer>
);

export default LoadingSpinner;
