// src/components/ScrollAnimations.js
import React, { useEffect, useState } from 'react';
import { gsap } from 'gsap';

const ScrollAnimations = () => {
    const [isVisible, setIsVisible] = useState(false);

    const handleScroll = () => {
        const triggerPoint = window.innerHeight * 0.8;
        const section = document.querySelector('.scroll-animation');

        if (section && section.getBoundingClientRect().top < triggerPoint) {
            setIsVisible(true);
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        if (isVisible) {
            gsap.to('.scroll-animation', {
                opacity: 1,
                y: 0,
                duration: 1.2,
                ease: 'power3.out',
                delay: 0.3,
                stagger: 0.2,
                onComplete: () => setIsVisible(true),
            });
        }
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [isVisible]);

    return (
        <section
            className={`scroll-animation ${isVisible ? 'visible' : ''}`}
            style={{
                padding: '3rem',
                opacity: isVisible ? 1 : 0,
                transform: 'translateY(30px)',
                transition: 'opacity 0.8s ease, transform 0.8s ease',
                textAlign: 'center',
                backgroundColor: 'rgba(240, 245, 255, 0.9)',
                borderRadius: '15px',
                boxShadow: isVisible
                    ? '0px 12px 30px rgba(0, 0, 0, 0.25)'
                    : '0px 6px 15px rgba(0, 0, 0, 0.1)',
                transitionProperty: 'box-shadow, opacity, transform',
            }}
        >
            <h2 style={{
                fontSize: '2.2rem',
                color: '#222',
                textShadow: '2px 2px 5px rgba(0, 0, 0, 0.15)',
                marginBottom: '1.2rem',
            }}>
                "Projects are the footprints of our passions and the milestones of our growth."
            </h2>
            <p style={{
                fontSize: '1.2rem',
                color: '#444',
                lineHeight: '1.6',
                maxWidth: '600px',
                margin: '0 auto',
            }}>
                Projects are not just tasksâ€”they are transformative experiences that sharpen skills, build resilience, and
                reveal our potential. Each project shapes who we become.
            </p>

            {/* Additional styles and animations */}
            <style jsx>{`
                .scroll-animation {
                    position: relative;
                    overflow: hidden;
                }

                .scroll-animation::before {
                    content: '';
                    position: absolute;
                    top: -50%;
                    left: -50%;
                    width: 200%;
                    height: 200%;
                    background: radial-gradient(circle, rgba(255, 255, 255, 0.2), transparent);
                    transform: scale(0);
                    transform-origin: center;
                    transition: transform 0.7s ease;
                }

                .scroll-animation.visible::before {
                    transform: scale(1);
                }

                .scroll-animation::after {
                    content: '';
                    position: absolute;
                    top: 0;
                    left: -100%;
                    width: 200%;
                    height: 100%;
                    background: linear-gradient(120deg, transparent, rgba(255, 255, 255, 0.3), transparent);
                    transform: translateX(0);
                    transition: transform 0.5s ease-in-out;
                }

                .scroll-animation.visible::after {
                    transform: translateX(100%);
                }

                /* Pulsating glow effect */
                .scroll-animation.visible h2 {
                    animation: glow 1.5s ease-in-out infinite alternate;
                }

                /* Floating animation */
                .scroll-animation.visible p {
                    animation: float 6s ease-in-out infinite alternate;
                }

                @keyframes glow {
                    from {
                        text-shadow: 0 0 10px rgba(255, 255, 255, 0.3), 0 0 20px rgba(255, 255, 255, 0.2);
                    }
                    to {
                        text-shadow: 0 0 20px rgba(255, 255, 255, 0.6), 0 0 30px rgba(255, 255, 255, 0.4);
                    }
                }

                @keyframes float {
                    from {
                        transform: translateY(0);
                    }
                    to {
                        transform: translateY(-10px);
                    }
                }
            `}</style>
        </section>
    );
};

export default ScrollAnimations;
