// src/pages/HomePage.js
import React, { useEffect } from 'react';
import { gsap } from 'gsap';
import HeroSection from '../components/HeroSection';
import AboutMe from '../components/AboutMe';
import Skills from '../components/Skills';
import MicroInteractions from '../components/MicroInteractions';
import KineticTypography from '../components/KineticTypography';

const HomePage = () => {
    useEffect(() => {
        gsap.fromTo('.intro-description', { opacity: 0, y: 20 }, { opacity: 1, y: 0, duration: 1, delay: 1 });
        gsap.from('.title', { opacity: 0, scale: 0.5, duration: 1, delay: 0.5 });
    }, []);

    return (
        <div className="home-page">
            <HeroSection />
            <section className="introduction" role="region" aria-labelledby="intro-title">
                <h2 id="intro-title" className="title typewriter-text">Welcome to My Portfolio!</h2>
                <p className="description intro-description">
                    I'm Udhaya Kumar A, a passionate Computer Science Engineer on a journey to innovate and create impactful solutions through technology.
                </p>
            </section>
            <Skills />
            <MicroInteractions />
            <KineticTypography />
            <AboutMe />
            
            {/* Inline CSS for advanced styles */}
            <style jsx>{`
                :root {
                    --text-color: #e0e0e0;
                    --accent-color: #00ffcc;
                    --background-gradient: linear-gradient(135deg, rgba(0,0,0,0.9), rgba(0,0,0,0.7)), url("/background.jpg") center/cover no-repeat;
                }

                body {
                    margin: 0;
                    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
                }

                .home-page {
                    color: var(--text-color);
                    background: var(--background-gradient);
                    padding: 2rem;
                }

                .introduction {
                    text-align: center;
                    margin: 4rem 0;
                    padding: 3rem 2rem;
                    background: rgba(255, 255, 255, 0.9);
                    box-shadow: 0 12px 20px rgba(0, 0, 0, 0.3);
                    border-radius: 15px;
                    animation: fadeIn 1.5s ease-in-out;
                }

                .title {
                    font-size: 3rem;
                    color: var(--accent-color);
                    margin-bottom: 1rem;
                    animation: typing 2s steps(40, end), blink 0.5s step-end infinite alternate;
                }

                .description {
                    font-size: 1.2rem;
                    line-height: 1.6;
                    max-width: 800px;
                    margin: auto;
                }

                @keyframes fadeIn {
                    from { opacity: 0; }
                    to { opacity: 1; }
                }

                @keyframes typing {
                    from { width: 0; }
                    to { width: 100%; }
                }

                .introduction:hover {
                   filter: brightness(1.05);
                   transition: filter 0.3s ease-in-out;
                }

                @media (max-width: 768px) {
                   .title {
                       font-size: 2.5rem; 
                   }

                   .description {
                       font-size: 1rem; 
                   }
               }
            `}</style>
        </div>
    );
};

export default HomePage;
