// src/components/maze.js
import React, { useEffect, useRef } from 'react';

const Maze = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');
        
        // Maze dimensions and configuration
        const cellSize = 40;
        const cols = 20;
        const rows = 15;
        const playerPosition = { x: 0, y: 0 };

        // Draw initial maze grid
        const drawMaze = () => {
            context.clearRect(0, 0, canvas.width, canvas.height);
            context.fillStyle = "#111";

            for (let y = 0; y < rows; y++) {
                for (let x = 0; x < cols; x++) {
                    // Draw cell walls based on your maze structure
                    context.strokeRect(x * cellSize, y * cellSize, cellSize, cellSize);
                }
            }
        };

        // Draw player position
        const drawPlayer = () => {
            context.fillStyle = "red";
            context.fillRect(
                playerPosition.x * cellSize,
                playerPosition.y * cellSize,
                cellSize,
                cellSize
            );
        };

        // Handle movement
        const movePlayer = (dx, dy) => {
            const newX = playerPosition.x + dx;
            const newY = playerPosition.y + dy;

            // Check boundaries and move player
            if (newX >= 0 && newX < cols && newY >= 0 && newY < rows) {
                playerPosition.x = newX;
                playerPosition.y = newY;
                drawMaze();
                drawPlayer();
            }
        };

        // Arrow key event listener
        const handleKeyDown = (e) => {
            switch (e.key) {
                case "ArrowUp":
                    movePlayer(0, -1);
                    break;
                case "ArrowDown":
                    movePlayer(0, 1);
                    break;
                case "ArrowLeft":
                    movePlayer(-1, 0);
                    break;
                case "ArrowRight":
                    movePlayer(1, 0);
                    break;
                default:
                    break;
            }
        };

        // Initial draw
        drawMaze();
        drawPlayer();

        // Event listener for keydown
        window.addEventListener("keydown", handleKeyDown);

        // Cleanup event listener on component unmount
        return () => window.removeEventListener("keydown", handleKeyDown);
    }, []);

    return (
        <div id="maze-game">
            <h2>Maze Game</h2>
            <canvas ref={canvasRef} id="mazeCanvas" width="800" height="600"></canvas>
            <div id="controls">
                <p>Use arrow keys to navigate the maze.</p>
            </div>
        </div>
    );
};

export default Maze;
