// src/components/HeroSection.js
import React, { useEffect } from 'react';
import { gsap } from 'gsap';

const HeroSection = () => {
    const styles = {
        hero: {
            position: 'relative',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            padding: '4rem 2rem',
            color: 'white',
            textAlign: 'center',
            overflow: 'hidden',
            height: '100vh', // Full-page height
            width: '100%',
            background: 'linear-gradient(135deg, #1c1c1c, #333)',
        },
        floatingObjects: {
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            overflow: 'hidden',
            zIndex: -1,
        },
        content: {
            position: 'relative',
            zIndex: 2,
            maxWidth: '800px'
        },
        title: {
            fontSize: '3.5rem',
            marginBottom: '0.5rem',
            color: '#ffd700',
            textShadow: '0px 0px 10px rgba(255,215,0,0.5)'
        },
        tagline: {
            fontSize: '1.3rem',
            color: '#ffd700',
            marginBottom: '1rem',
        },
        description: {
            fontSize: '1.1rem',
            margin: '0.5rem 0 2rem',
            lineHeight: 1.6,
        },
        buttonContainer: {
            display: 'flex',
            justifyContent: 'center',
            gap: '1rem'
        },
        primaryButton: {
            padding: '0.75rem 1.5rem',
            borderRadius: '5px',
            fontWeight: 'bold',
            backgroundColor: '#ffd700',
            color: '#333',
            transition: 'transform 0.3s, box-shadow 0.3s',
        },
        secondaryButton: {
            padding: '0.75rem 1.5rem',
            borderRadius: '5px',
            fontWeight: 'bold',
            backgroundColor: '#333',
            color: '#ffd700',
            border: '1px solid #ffd700',
            transition: 'transform 0.3s, box-shadow 0.3s',
        }
    };

    useEffect(() => {
        gsap.fromTo('.hero-title', { opacity: 0, y: -30 }, { opacity: 1, y: 0, duration: 1 });
        gsap.fromTo('.hero-button', { opacity: 0, scale: 0.9 }, { opacity: 1, scale: 1, duration: 0.4, stagger: 0.2 });
    }, []);

    return (
        <section style={styles.hero}>
            {/* Floating Objects Animation */}
            <div style={styles.floatingObjects}>
                <div className="object object-1"></div>
                <div className="object object-2"></div>
                <div className="object object-3"></div>
                <div className="object object-4"></div>
                <div className="object object-5"></div>
            </div>

            <div style={styles.content}>
                <h1 className="hero-title" style={styles.title}>Hello, I'm Udhaya Kumar A</h1>
                <p style={styles.tagline}>Aspiring Public Speaker, AI Enthusiast, and Technology Innovator</p>
                <p style={styles.description}>
                    A Computer Science Engineering student passionate about harnessing the power of AI and technology to solve real-world challenges.
                </p>
                <div style={styles.buttonContainer}>
                    <a href="#projects" className="hero-button" style={styles.primaryButton}>View Projects</a>
                    <a href="/path/to/resume.pdf" target="_blank" className="hero-button" style={styles.secondaryButton}>Download Resume</a>
                </div>
            </div>

            {/* Inline Keyframes and Styling for Floating Objects */}
            <style>
                {`
                    .object {
                        position: absolute;
                        border-radius: 50%;
                        background: rgba(255, 215, 0, 0.2);
                        opacity: 0.8;
                        animation: floatAnimation 10s linear infinite alternate;
                    }
                    .object-1 { width: 80px; height: 80px; top: 20%; left: 10%; }
                    .object-2 { width: 120px; height: 120px; top: 60%; left: 30%; }
                    .object-3 { width: 100px; height: 100px; top: 40%; left: 70%; }
                    .object-4 { width: 60px; height: 60px; top: 10%; left: 85%; }
                    .object-5 { width: 90px; height: 90px; top: 70%; left: 50%; }
                    
                    @keyframes floatAnimation {
                        0% { transform: translateY(0) translateX(0); }
                        100% { transform: translateY(-20px) translateX(10px); }
                    }
                `}
            </style>
        </section>
    );
};

export default HeroSection;
