// src/components/ScrollAnimations.js
import React, { useEffect, useState } from 'react';
import '../styles/ScrollAnimations.css';

const ScrollAnimations = () => {
    const [isVisible, setIsVisible] = useState(false);

    const handleScroll = () => {
        const triggerPoint = window.innerHeight * 0.75;
        const section = document.querySelector('.scroll-animation');

        if (section && section.getBoundingClientRect().top < triggerPoint) {
            setIsVisible(true);
        } else {
            setIsVisible(false);
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <section className={`scroll-animation ${isVisible ? 'visible' : ''}`}>
            <h2>Scroll Down to See the Animation</h2>
            <p>This text fades in as you scroll!</p>
        </section>
    );
};

export default ScrollAnimations;
