// src/components/MicroInteractions.js
import React, { useEffect } from 'react';

const MicroInteractions = () => {
    useEffect(() => {
        // Load the Snake game script
        const script = document.createElement('script');
        script.src = '/assets/javascript-snakes-master/snakes.js'; // Path to the Snake game script
        script.async = true;
        document.body.appendChild(script);

        // Cleanup function to remove the script when the component unmounts
        return () => {
            document.body.removeChild(script);
        };
    }, []);

    return (
        <div id="snakes">
            <h2>Snake Game</h2>
            <div id="score">
                <span class="current"><span class='label'>Score: </span><span class="value">000000</span></span>
                <span class="high"><span class='label'>High: </span><span class="value">000000</span></span>
            </div>
            <canvas id="canvas">
                <div className='unsupported'>
                    Sorry, this example cannot be run because your browser does not support the &lt;canvas&gt; element
                </div>
            </canvas>
            <div id="overlay">
                <div id="loading" className='menu' style={{ display: 'none' }}>
                    <h1>Loading...</h1>
                </div>
            </div>
            <div id="help">
                ( use the arrow and enter keys for control )
            </div>
        </div>
    );
};

export default MicroInteractions;
