import React, { useEffect } from 'react';

const ContactPage = () => {
    useEffect(() => {
        // Create custom cursor
        const cursor = document.createElement('div');
        cursor.className = 'cursor';
        document.body.appendChild(cursor);

        // Move cursor with mouse
        const moveCursor = (e) => {
            cursor.style.left = `${e.clientX}px`;
            cursor.style.top = `${e.clientY}px`;
        };

        // Add event listener for mouse movement
        window.addEventListener('mousemove', moveCursor);

        // Change cursor style on hover
        const interactiveElements = document.querySelectorAll('.contact-form input, .contact-form textarea, .contact-form button');

        interactiveElements.forEach((element) => {
            element.addEventListener('mouseenter', () => {
                cursor.style.transform = 'scale(1.5)'; // Grow cursor on hover
                cursor.style.background = 'rgba(255, 165, 0, 0.8)'; // Change color on hover
            });

            element.addEventListener('mouseleave', () => {
                cursor.style.transform = 'scale(1)'; // Reset cursor on leave
                cursor.style.background = 'rgba(0, 188, 212, 0.8)'; // Reset color
            });
        });

        // Cleanup on component unmount
        return () => {
            window.removeEventListener('mousemove', moveCursor);
            document.body.removeChild(cursor);
            interactiveElements.forEach((element) => {
                element.removeEventListener('mouseenter', () => {});
                element.removeEventListener('mouseleave', () => {});
            });
        };
    }, []);

    return (
        <div className="contact-page">
            <h1>Contact Me</h1>
            <form className="contact-form" action="https://formspree.io/f/xgedqnry" method="POST">
                <label htmlFor="name">Name</label>
                <input type="text" id="name" name="name" required />

                <label htmlFor="email">Email</label>
                <input type="email" id="email" name="email" required />

                <label htmlFor="message">Message</label>
                <textarea id="message" name="message" required></textarea>

                <button type="submit">Send Message</button>
            </form>

            {/* Inline CSS for advanced styles */}
            <style jsx>{`
                :root {
                    --primary-color: #007bff;
                    --background-color: #121212; /* Dark background */
                    --form-background: rgba(255, 255, 255, 0.1); /* Slightly transparent form background */
                    --text-color: #ffffff; /* White text color */
                    --border-color: #444; /* Darker border color */
                    --accent-color: #ffd700; /* Accent color */
                }

                body {
                    margin: 0;
                    font-family: 'Arial', sans-serif;
                    overflow: hidden; /* Prevent scrollbars if necessary */
                }

                .contact-page {
                    padding: 2rem;
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    background: var(--background-color);
                    min-height: 100vh; /* Full height for better layout */
                }

                h1 {
                    font-size: 2.5rem;
                    text-align: center;
                    color: var(--text-color);
                    margin-bottom: 2rem; /* Space below the title */
                }

                .contact-form {
                    background: var(--form-background);
                    border-radius: 10px;
                    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.5);
                    padding: 2rem;
                    width: 100%;
                    max-width: 500px;
                    display: flex;
                    flex-direction: column;
                    gap: 1.5rem; /* Space between form elements */
                }

                label {
                    font-weight: bold;
                    color: var(--text-color);
                }

                input, textarea {
                    padding: 0.5rem;
                    border: 1px solid var(--border-color);
                    border-radius: 4px;
                    transition: border-color 0.3s, box-shadow 0.3s; /* Smooth transitions */
                    background: rgba(255, 255, 255, 0.2); /* Light background for inputs */
                    color: var(--text-color); /* White text in inputs */
                }

                input:focus, textarea:focus {
                    border-color: var(--primary-color);
                    box-shadow: 0 0 5px rgba(0, 123, 255, 0.5); /* Highlight on focus */
                }

                button {
                    padding: 0.5rem 1rem;
                    background-color: var(--primary-color);
                    color: #fff;
                    border: none;
                    border-radius: 4px;
                    cursor: pointer;
                    transition: background-color 0.3s, transform 0.3s; /* Button animations */
                }

                button:hover {
                    background-color: var(--accent-color);
                    transform: scale(1.05); /* Slightly enlarge on hover */
                }

                /* Custom cursor styles */
                .cursor {
                    position: fixed;
                    width: 20px; /* Size of the cursor */
                    height: 20px;
                    background: rgba(0, 188, 212, 0.8); /* Cursor color */
                    border-radius: 50%; /* Round cursor */
                    pointer-events: none; /* Allow click events to go through */
                    transform: translate(-50%, -50%); /* Center the cursor */
                    transition: transform 0.1s ease, background 0.3s ease; /* Smooth transitions */
                    z-index: 1000; /* Above other elements */
                }
            `}</style>
        </div>
    );
};

export default ContactPage;
