// src/components/LoadingSpinner.js
import React from 'react';
import styled, { keyframes } from 'styled-components';

// Keyframes for pulsing neon effect on the outline
const pulseOutline = keyframes`
    0%, 100% { box-shadow: 0 0 10px #00e6e6, 0 0 20px #00e6e6, 0 0 30px #00e6e6, 0 0 40px #00e6e6; }
    50% { box-shadow: 0 0 20px #00e6e6, 0 0 30px #00e6e6, 0 0 40px #00e6e6, 0 0 60px #00e6e6; }
`;

// Keyframes for inner neon pulse animation
const innerPulse = keyframes`
    0%, 100% { opacity: 0.6; transform: scale(1); }
    50% { opacity: 1; transform: scale(1.2); }
`;

// Container for the loading spinner
const SpinnerContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100vh;
    background-color: #1a1a1a;
`;

// Non-tilted heart outline with a neon pulse effect
const HeartOutline = styled.div`
    position: relative;
    width: 100px;
    height: 90px;
    border: 5px solid #00e6e6;  /* Neon outline */
    border-radius: 50% 50% 0 0;
    border-top-left-radius: 100px;
    border-top-right-radius: 100px;
    background-color: black;
    box-shadow: 0 0 10px #00e6e6, 0 0 20px #00e6e6, 0 0 30px #00e6e6;
    animation: ${pulseOutline} 2s infinite;
    overflow: hidden;

    &::after {
        content: '';
        position: absolute;
        width: 100px;
        height: 90px;
        border: 5px solid #00e6e6;
        border-radius: 50% 50% 0 0;
        border-top-left-radius: 100px;
        border-top-right-radius: 100px;
        top: 90px;
        background-color: black;
    }
`;

// Inner pulse animation inside the hollow heart
const InnerPulse = styled.div`
    position: absolute;
    top: 50%;
    left: 50%;
    width: 60px;
    height: 60px;
    background-color: rgba(0, 230, 230, 0.3);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    animation: ${innerPulse} 1.5s infinite;
    box-shadow: 0 0 10px #00e6e6, 0 0 20px #00e6e6;
`;

// Loading text with animated neon glow effect
const LoadingText = styled.p`
    color: #00e6e6;
    font-size: 1.2rem;
    font-weight: bold;
    margin-top: 20px;
    animation: ${pulseOutline} 2s infinite;
`;

const LoadingSpinner = () => (
    <SpinnerContainer>
        <HeartOutline>
            <InnerPulse />
        </HeartOutline>
        <LoadingText>Loading...</LoadingText>
    </SpinnerContainer>
);

export default LoadingSpinner;
