// src/pages/HomePage.js
import React, { useEffect } from 'react';
import HeroSection from '../components/HeroSection';
import AboutMe from '../components/AboutMe';
import Skills from '../components/Skills';
import MicroInteractions from '../components/MicroInteractions';
import KineticTypography from '../components/KineticTypography';
import { initScrollAnimations } from '../animations/scrollAnimations';
import { buttonHoverEffect } from '../animations/microInteractions';
import { typewriterEffect } from '../animations/kineticTypography';
import './HomePage.css';

const HomePage = () => {
    useEffect(() => {
        initScrollAnimations();                // Initialize scroll animations
        buttonHoverEffect('.interactive-btn'); // Apply hover effects to buttons with this class
        typewriterEffect('.typewriter-text', 'Welcome to My Portfolio!'); // Typewriter effect for welcome text
    }, []);

    return (
        <div className="home-page">
            <HeroSection />
            <section className="introduction">
                <h2 className="typewriter-text">Welcome to My Portfolio!</h2>
                <p>
                    I'm Udhaya Kumar A, a passionate Computer Science Engineer on a journey to innovate and create impactful solutions through technology. 
                    Explore my work, skills, and experiences as I aim to bridge the gap between ideas and execution.
                </p>
            </section>
            <Skills />
            <MicroInteractions />
            <KineticTypography />
            <AboutMe />
        </div>
    );
};

export default HomePage;
