// src/components/AboutMe.js
import React from 'react';
import { gsap } from 'gsap';

const AboutMe = () => {
    const styles = {
        about: {
            padding: '4rem 2rem',
            backgroundColor: 'transparent',
            color: 'var(--text-color)',
            textAlign: 'center'
        },
        title: {
            fontSize: '2.5rem',
            color: 'var(--accent-color)',
            textShadow: '1px 1px 3px rgba(0, 0, 0, 0.2)',
            marginBottom: '1.5rem'
        },
        achievementsList: {
            listStyle: 'none',
            padding: 0,
            maxWidth: '800px',
            margin: '2.5rem auto',
            textAlign: 'left',
            display: 'flex',
            flexDirection: 'column',
            gap: '1rem'
        },
        achievementItem: {
            padding: '1.2rem',
            background: 'rgba(255, 255, 255, 0.1)',
            backdropFilter: 'blur(6px)',
            borderRadius: '8px',
            border: '1px solid rgba(255, 255, 255, 0.2)',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
            fontSize: '1.1rem',
            color: '#f1f1f1',
            transform: 'scale(1)',
            transition: 'transform 0.2s ease'
        }
    };

    React.useEffect(() => {
        gsap.fromTo('.achievementItem', { opacity: 0, y: 50 }, { opacity: 1, y: 0, stagger: 0.2, duration: 0.8 });
    }, []);

    return (
        <section style={styles.about}>
            <h2 style={styles.title}>About Me</h2>
            <ul style={styles.achievementsList}>
                <li style={styles.achievementItem} className="achievementItem">Achievement 1</li>
                <li style={styles.achievementItem} className="achievementItem">Achievement 2</li>
                {/* Add more achievements as needed */}
            </ul>
        </section>
    );
};

export default AboutMe;
