// src/components/MicroInteractions.js
import React, { useEffect } from 'react';

const MicroInteractions = () => {
    useEffect(() => {
        // Load the maze game script
        const script = document.createElement('script');
        script.src = '/src/components/maze-game.js'; // Path to your maze game script
        script.async = true;
        document.body.appendChild(script);

        // Cleanup function to remove the script when the component unmounts
        return () => {
            document.body.removeChild(script);
        };
    }, []);

    return (
        <div id="maze-game">
            <h2>Maze Game</h2>
            <div id="maze-container">
                <canvas id="mazeCanvas" width="800" height="600"></canvas>
                <div id="controls">
                    <p>Use arrow keys to navigate the maze.</p>
                </div>
            </div>
            <div id="score">
                <span className="current"><span className='label'>Score: </span><span className="value">0</span></span>
            </div>
            <div id="help">
                ( Use the arrow keys to move through the maze )
            </div>
        </div>
    );
};

export default MicroInteractions;
