// src/components/LoadingSpinner.js
import React from 'react';
import styled, { keyframes } from 'styled-components';

// Keyframes for EKG-like pulse animation
const ekgAnimation = keyframes`
  0% { transform: translateX(0) scaleY(0.2); }
  5% { transform: translateX(5%) scaleY(0.5); }
  
  /* P Wave */
  10% { transform: translateX(10%) scaleY(0.8); }
  15% { transform: translateX(15%) scaleY(0.4); }
  
  /* QRS Complex */
  20% { transform: translateX(20%) scaleY(1.5); } /* Sharp peak */
  25% { transform: translateX(25%) scaleY(-1); } /* Sharp drop */
  30% { transform: translateX(30%) scaleY(0.5); } /* Slight rise */

  /* T Wave */
  40% { transform: translateX(40%) scaleY(1); }
  50% { transform: translateX(50%) scaleY(0.2); }

  /* Flatline between beats */
  60%, 100% { transform: translateX(60%) scaleY(0.2); }
`;

// Container for the loading spinner
const SpinnerContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100vh;
    background-color: #1a1a1a; /* Dark background */
`;

// EKG line representing the heartbeat
const EKGLine = styled.div`
    width: 200px; /* Length of the EKG line */
    height: 10px; /* Initial height of the line */
    background-color: transparent; /* Transparent background */
    border-left: 2px solid #00e6e6; /* Neon color for the line */
    position: relative;
    animation: ${ekgAnimation} 2s linear infinite; /* EKG animation */
    box-shadow: 
        -5px -5px rgba(0,230,230,0.8), 
        inset -2px -2px rgba(255,255,255,0.3); /* Neon glow effect */
`;

// Loading text with neon glow effect
const LoadingText = styled.p`
    color: #00e6e6;
    font-size: 1.2rem;
    font-weight: bold;
    margin-top: 20px;
    text-shadow:
        0 0 5px #00e6e6,
        0 0 10px #00e6e6; /* Neon glow for text */
`;

const LoadingSpinner = () => (
    <SpinnerContainer>
        <EKGLine />
        <LoadingText>Loading...</LoadingText>
    </SpinnerContainer>
);

export default LoadingSpinner;
