// src/components/LoadingSpinner.js
import React from 'react';
import styled, { keyframes } from 'styled-components';

// Keyframes for the neon glow effect on the heart outline
const outlineGlow = keyframes`
    0%, 100% { box-shadow: 0 0 10px #00e6e6, 0 0 20px #00e6e6, 0 0 30px #00e6e6; }
    50% { box-shadow: 0 0 15px #00e6e6, 0 0 25px #00e6e6, 0 0 35px #00e6e6; }
`;

// Keyframes for inner pulse animation within the heart outline
const innerPulse = keyframes`
    0%, 100% { transform: scale(0.8); opacity: 0.6; }
    50% { transform: scale(1); opacity: 1; }
`;

// Container for the loading spinner
const SpinnerContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100vh;
    background-color: #1a1a1a;
`;

// Heart outline with neon glow effect
const HeartOutline = styled.div`
    position: relative;
    width: 120px;
    height: 100px;
    border: 5px solid transparent;
    border-radius: 50% 50% 0 0;
    background-color: black;
    box-shadow: 0 0 10px #00e6e6, inset 0 0 10px #00e6e6;
    animation: ${outlineGlow} 2s infinite alternate;
    transform: translateY(-50px);

    &::before,
    &::after {
        content: '';
        position: absolute;
        background-color: black;
        width: 100%;
        height: 100%;
        border-radius: 50%;
    }

    &::before {
        top: -50px;
        left: 0;
    }

    &::after {
        top: 0;
        left: 60px;
    }
`;

// Inner pulse effect to simulate heartbeat within the neon heart
const InnerPulse = styled.div`
    position: absolute;
    top: 50%;
    left: 50%;
    width: 50px;
    height: 50px;
    background-color: rgba(0, 230, 230, 0.4);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    animation: ${innerPulse} 1.5s infinite;
    box-shadow: 0 0 10px rgba(0, 230, 230, 0.6), 0 0 20px rgba(0, 230, 230, 0.8);
`;

// Loading text with neon glow effect
const LoadingText = styled.p`
    color: #00e6e6;
    font-size: 1.2rem;
    font-weight: bold;
    margin-top: 20px;
    text-shadow: 0 0 5px #00e6e6, 0 0 10px #00e6e6;
    animation: ${outlineGlow} 2s infinite alternate;
`;

const LoadingSpinner = () => (
    <SpinnerContainer>
        <HeartOutline>
            <InnerPulse />
        </HeartOutline>
        <LoadingText>Loading...</LoadingText>
    </SpinnerContainer>
);

export default LoadingSpinner;
