// src/components/ScrollAnimations.js
import React, { useEffect, useState } from 'react';

const ScrollAnimations = () => {
    const [isVisible, setIsVisible] = useState(false);

    const handleScroll = () => {
        const triggerPoint = window.innerHeight * 0.75;
        const section = document.querySelector('.scroll-animation');

        if (section && section.getBoundingClientRect().top < triggerPoint) {
            setIsVisible(true);
        } else {
            setIsVisible(false);
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <section className={`scroll-animation ${isVisible ? 'visible' : ''}`} style={{
            padding: '2rem',
            opacity: isVisible ? 1 : 0,
            transform: isVisible ? 'translateY(0)' : 'translateY(20px)',
            transition: 'opacity 0.5s ease, transform 0.5s ease',
            textAlign: 'center',
            backgroundColor: '#f8f8fc'
        }}>
            <h2 style={{
                fontSize: '1.8rem',
                color: '#333'
            }}>Scroll Down to See the Animation</h2>
            <p style={{
                fontSize: '1rem',
                color: '#555'
            }}>This text fades in as you scroll!</p>
        </section>
    );
};

export default ScrollAnimations;
