// src/components/HeroSection.js
import React, { useEffect } from 'react';
import { gsap } from 'gsap';

const HeroSection = () => {
    const styles = {
        hero: {
            position: 'relative',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            padding: '3rem 1.5rem',
            color: 'white',
            textAlign: 'center',
            overflow: 'hidden',
            height: '60vh',  // Smaller height for a more compact section
            maxWidth: '600px',
            margin: '0 auto',
            borderRadius: '15px',
            background: 'linear-gradient(135deg, #1c1c1c, #333)',
            boxShadow: '0 8px 20px rgba(0, 0, 0, 0.3)',
        },
        backgroundAnimation: {
            position: 'absolute',
            top: '-50%',
            left: '-50%',
            width: '200%',
            height: '200%',
            background: 'radial-gradient(circle, #333, #1c1c1c)',
            opacity: 0.5,
            animation: 'pulse 10s infinite alternate ease-in-out',
            zIndex: -2,
            filter: 'blur(20px)',
        },
        content: {
            position: 'relative',
            zIndex: 2,
        },
        title: {
            fontSize: '2.5rem',
            marginBottom: '0.5rem',
            color: '#ffd700',
            textShadow: '0px 0px 10px rgba(255,215,0,0.5)',
        },
        tagline: {
            fontSize: '1.2rem',
            color: '#ffd700',
            marginBottom: '1rem',
        },
        description: {
            fontSize: '1rem',
            margin: '0.5rem 0 1.5rem',
            lineHeight: 1.4,
        },
        buttonContainer: {
            display: 'flex',
            justifyContent: 'center',
            gap: '0.75rem',
        },
        primaryButton: {
            padding: '0.6rem 1.2rem',
            borderRadius: '5px',
            fontWeight: 'bold',
            backgroundColor: '#ffd700',
            color: '#333',
            transition: 'transform 0.3s, box-shadow 0.3s',
        },
        secondaryButton: {
            padding: '0.6rem 1.2rem',
            borderRadius: '5px',
            fontWeight: 'bold',
            backgroundColor: '#333',
            color: '#ffd700',
            border: '1px solid #ffd700',
            transition: 'transform 0.3s, box-shadow 0.3s',
        }
    };

    useEffect(() => {
        gsap.fromTo('.hero-title', { opacity: 0, y: -30 }, { opacity: 1, y: 0, duration: 1 });
        gsap.fromTo('.hero-button', { opacity: 0, scale: 0.9 }, { opacity: 1, scale: 1, duration: 0.4, stagger: 0.2 });
    }, []);

    return (
        <section style={styles.hero}>
            {/* Animated Background */}
            <div style={styles.backgroundAnimation}></div>

            <div style={styles.content}>
                <h1 className="hero-title" style={styles.title}>Hello, I'm Udhaya Kumar A</h1>
                <p style={styles.tagline}>Aspiring Public Speaker, AI Enthusiast, and Technology Innovator</p>
                <p style={styles.description}>
                    A Computer Science Engineering student passionate about harnessing the power of AI and technology to solve real-world challenges.
                </p>
                <div style={styles.buttonContainer}>
                    <a href="#projects" className="hero-button" style={styles.primaryButton}>View Projects</a>
                    <a href="/path/to/resume.pdf" target="_blank" className="hero-button" style={styles.secondaryButton}>Download Resume</a>
                </div>
            </div>

            {/* Inline Keyframes for Background Animation */}
            <style>
                {`
                    @keyframes pulse {
                        0% { transform: scale(1); }
                        100% { transform: scale(1.1); }
                    }
                `}
            </style>
        </section>
    );
};

export default HeroSection;
