// src/components/HeroSection.js
import React from 'react';

const HeroSection = () => {
    const styles = {
        hero: {
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            padding: '4rem 2rem',
            background: 'url("/hi.jpg") no-repeat center center/cover',
            color: 'white',
            textAlign: 'center'
        },
        content: {
            maxWidth: '800px'
        },
        title: {
            fontSize: '2.5rem',
            marginBottom: '0.5rem'
        },
        tagline: {
            fontSize: '1.2rem',
            color: '#ffd700'
        },
        description: {
            margin: '1rem 0 2rem',
            fontSize: '1rem',
            lineHeight: 1.6
        },
        buttonContainer: {
            margin: '0 0.5rem'
        },
        primaryButton: {
            margin: '0 0.5rem',
            padding: '0.75rem 1.5rem',
            borderRadius: '5px',
            textDecoration: 'none',
            fontWeight: 'bold',
            backgroundColor: '#ffd700',
            color: '#333'
        },
        secondaryButton: {
            margin: '0 0.5rem',
            padding: '0.75rem 1.5rem',
            borderRadius: '5px',
            textDecoration: 'none',
            fontWeight: 'bold',
            backgroundColor: '#333',
            color: '#ffd700',
            border: '1px solid #ffd700'
        }
    };

    return (
        <section style={styles.hero}>
            <div style={styles.content}>
                <h1 style={styles.title}>Hello, I'm Udhaya Kumar A</h1>
                <p style={styles.tagline}>Aspiring Public Speaker, AI Enthusiast, and Technology Innovator</p>
                <p style={styles.description}>
                    A Computer Science Engineering student passionate about harnessing the power of AI and technology to solve real-world challenges.
                </p>
                <div style={styles.buttonContainer}>
                    <a href="#projects" style={styles.primaryButton}>View Projects</a>
                    <a href="/path/to/resume.pdf" target="_blank" style={styles.secondaryButton}>Download Resume</a>
                </div>
            </div>
        </section>
    );
};

export default HeroSection;
