// src/components/MicroInteractions.js
import React, { useState } from 'react';
import '../styles/MicroInteractions.css';

const MicroInteractions = () => {
    const [toggle, setToggle] = useState(false);

    const handleToggle = () => {
        setToggle(!toggle);
    };

    return (
        <section className="micro-interactions">
            <h2>Micro Interactions</h2>
            <div className="button-container">
                <button className="micro-button">Hover Over Me!</button>
            </div>
            <div className="toggle-container">
                <button className={`toggle-button ${toggle ? 'active' : ''}`} onClick={handleToggle}>
                    {toggle ? 'Toggle ON' : 'Toggle OFF'}
                </button>
            </div>
        </section>
    );
};

export default MicroInteractions;
