// src/components/ScrollAnimations.js
import React, { useEffect, useState } from 'react';
import { gsap } from 'gsap';

const ScrollAnimations = () => {
    const [isVisible, setIsVisible] = useState(false);

    const handleScroll = () => {
        const triggerPoint = window.innerHeight * 0.8;
        const section = document.querySelector('.scroll-animation');

        if (section && section.getBoundingClientRect().top < triggerPoint) {
            setIsVisible(true);
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        if (isVisible) {
            gsap.to('.scroll-animation', {
                opacity: 1,
                y: 0,
                duration: 1,
                ease: 'power3.out',
                delay: 0.3,
                stagger: 0.15,
                onComplete: () => setIsVisible(true),
            });
        }
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [isVisible]);

    return (
        <section
            className={`scroll-animation ${isVisible ? 'visible' : ''}`}
            style={{
                padding: '2.5rem',
                opacity: isVisible ? 1 : 0,
                transform: 'translateY(30px)',
                transition: 'opacity 0.7s ease, transform 0.7s ease',
                textAlign: 'center',
                backgroundColor: '#f0f5ff',
                borderRadius: '10px',
                boxShadow: isVisible
                    ? '0px 8px 20px rgba(0, 0, 0, 0.2)'
                    : '0px 4px 15px rgba(0, 0, 0, 0.1)',
                transitionProperty: 'box-shadow, opacity, transform',
            }}
        >
            <h2 style={{
                fontSize: '2rem',
                color: '#333',
                textShadow: '1px 1px 3px rgba(0, 0, 0, 0.15)',
                marginBottom: '1rem',
            }}>Interactive Section</h2>
            <p style={{
                fontSize: '1.1rem',
                color: '#666',
                lineHeight: '1.5',
            }}>This section appears with smooth animations as you scroll down, creating a dynamic visual experience.</p>

            {/* Additional styles and animations */}
            <style jsx>{`
                .scroll-animation {
                    position: relative;
                    overflow: hidden;
                }

                .scroll-animation::after {
                    content: '';
                    position: absolute;
                    top: 0;
                    left: -100%;
                    width: 200%;
                    height: 100%;
                    background: linear-gradient(120deg, transparent, rgba(255, 255, 255, 0.4), transparent);
                    transition: transform 0.5s ease-in-out;
                    transform: scaleX(0);
                }

                .scroll-animation.visible::after {
                    transform: scaleX(1);
                }
            `}</style>
        </section>
    );
};

export default ScrollAnimations;
