// src/pages/HomePage.js
import React, { useEffect } from 'react';
import HeroSection from '../components/HeroSection';
import AboutMe from '../components/AboutMe';
import Skills from '../components/Skills';
import MicroInteractions from '../components/MicroInteractions';
import KineticTypography from '../components/KineticTypography';

const HomePage = () => {
    const styles = {
        homePage: {
            fontFamily: 'Arial, sans-serif',
            color: 'var(--text-color)',
            background: 'transparent',
            padding: '2rem'
        },
        introduction: {
            textAlign: 'center',
            margin: '4rem 0',
            padding: '2rem',
            background: 'rgba(255, 255, 255, 0.9)',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
            borderRadius: 'var(--border-radius)'
        },
        title: {
            fontSize: '2.5rem',
            color: 'var(--accent-color)',
            marginBottom: '1rem'
        },
        description: {
            fontSize: '1.2rem',
            lineHeight: 1.6,
            maxWidth: '800px',
            margin: '0 auto',
            color: 'var(--text-color)'
        }
    };

    useEffect(() => {
        // Your existing useEffect code
    }, []);

    return (
        <div style={styles.homePage}>
            <HeroSection />
            <section style={styles.introduction}>
                <h2 style={styles.title} className="typewriter-text">Welcome to My Portfolio!</h2>
                <p style={styles.description}>
                    I'm Udhaya Kumar A, a passionate Computer Science Engineer on a journey to innovate and create impactful solutions through technology.
                </p>
            </section>
            <Skills />
            <MicroInteractions />
            <KineticTypography />
            <AboutMe />
        </div>
    );
};

export default HomePage;
