// src/components/MicroInteractions.js
import React, { useState } from 'react';
import { gsap } from 'gsap';

const MicroInteractions = () => {
    const [toggle, setToggle] = useState(false);
    const [loading, setLoading] = useState(false);

    const handleToggle = () => {
        setToggle(!toggle);
        gsap.to(".toggle-button", {
            backgroundColor: toggle ? '#ddd' : '#1e90ff',
            color: toggle ? 'black' : 'white',
            duration: 0.3
        });
    };

    const handleHoverIn = (e) => {
        gsap.to(e.target, {
            scale: 1.1,
            backgroundColor: '#eee',
            duration: 0.2,
        });
    };

    const handleHoverOut = (e) => {
        gsap.to(e.target, {
            scale: 1,
            backgroundColor: '#ddd',
            duration: 0.2,
        });
    };

    const handleLoading = () => {
        setLoading(true);
        gsap.to(".loading-button", { scale: 1.1, duration: 0.2 });
        
        // Simulate a loading process
        setTimeout(() => {
            setLoading(false);
            gsap.to(".loading-button", { scale: 1, duration: 0.2 });
            alert("Loading complete!");
        }, 2000); // Simulate loading for 2 seconds
    };

    return (
        <section style={{
            padding: '2rem',
            textAlign: 'center',
            backgroundColor: '#f4f4f9'
        }}>
            <h2 style={{
                fontSize: '1.8rem',
                color: '#333',
                marginBottom: '1rem'
            }}>Micro Interactions</h2>
            
            <div style={{ marginBottom: '1rem' }}>
                <button
                    style={{
                        padding: '0.8rem 1.2rem',
                        fontSize: '1rem',
                        backgroundColor: '#ddd',
                        border: '1px solid #bbb',
                        borderRadius: '6px',
                        transition: 'background-color 0.3s',
                        cursor: 'pointer',
                        transformOrigin: 'center'
                    }}
                    onMouseEnter={handleHoverIn}
                    onMouseLeave={handleHoverOut}
                >
                    Hover Over Me!
                </button>
            </div>

            <div>
                <button
                    className="toggle-button"
                    style={{
                        padding: '0.8rem 1.2rem',
                        fontSize: '1rem',
                        backgroundColor: toggle ? '#1e90ff' : '#ddd',
                        color: toggle ? 'white' : 'black',
                        border: '1px solid #bbb',
                        borderRadius: '6px',
                        cursor: 'pointer',
                        transition: 'background-color 0.3s, color 0.3s',
                    }}
                    onClick={handleToggle}
                    aria-pressed={toggle}
                >
                    {toggle ? 'Toggle ON' : 'Toggle OFF'}
                </button>
            </div>

            <div style={{ marginTop: '2rem' }}>
                <button
                    className="loading-button"
                    style={{
                        padding: '0.8rem 1.2rem',
                        fontSize: '1rem',
                        backgroundColor: '#28a745', // Green for loading button
                        color: 'white',
                        borderRadius: '6px',
                        border: 'none',
                        cursor: loading ? 'not-allowed' : 'pointer',
                        opacity: loading ? 0.6 : 1,
                    }}
                    onClick={handleLoading}
                    disabled={loading} // Disable button while loading
                >
                    {loading ? "Loading..." : "Start Loading"}
                </button>
            </div>
        </section>
    );
};

export default MicroInteractions;
