// src/components/LoadingSpinner.js
import React from 'react';
import styled, { keyframes } from 'styled-components';

// Keyframes for EKG-like pulse animation
const ekgAnimation = keyframes`
    0%, 100% { transform: translateX(0); height: 10px; }
    10% { transform: translateX(20px); height: 30px; }
    20% { transform: translateX(40px); height: 10px; }
    30% { transform: translateX(60px); height: 50px; }
    40% { transform: translateX(80px); height: 10px; }
    50% { transform: translateX(100px); height: 30px; }
    60% { transform: translateX(120px); height: 10px; }
    70% { transform: translateX(140px); height: 50px; }
    80% { transform: translateX(160px); height: 10px; }
    90% { transform: translateX(180px); height: 30px; }
`;

// Container for the loading spinner
const SpinnerContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100vh;
    background-color: #1a1a1a; /* Dark background */
`;

// EKG line representing the heartbeat
const EKGLine = styled.div`
    width: 10px;
    background-color: #00e6e6; /* Neon color */
    position: relative;
    bottom: 0;
    animation: ${ekgAnimation} 1.5s linear infinite;
`;

// Loading text with neon glow effect
const LoadingText = styled.p`
    color: #00e6e6;
    font-size: 1.2rem;
    font-weight: bold;
    margin-top: 20px;
    text-shadow:
        0 0 5px #00e6e6,
        0 0 10px #00e6e6; /* Neon glow for text */
`;

const LoadingSpinner = () => (
    <SpinnerContainer>
        <EKGLine />
        <LoadingText>Loading...</LoadingText>
    </SpinnerContainer>
);

export default LoadingSpinner;
