// src/components/LoadingSpinner.js
import React from 'react';
import styled, { keyframes } from 'styled-components';

// Keyframes for pulsing heartbeat effect
const pulse = keyframes`
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.1); }
`;

// Keyframes for neon glow animation
const neonGlow = keyframes`
    0% { text-shadow: 0 0 5px #00e6e6, 0 0 10px #00e6e6, 0 0 15px #00e6e6, 0 0 20px #00e6e6; }
    50% { text-shadow: 0 0 10px #00e6e6, 0 0 20px #00e6e6, 0 0 30px #00e6e6, 0 0 40px #00e6e6; }
    100% { text-shadow: 0 0 5px #00e6e6, 0 0 10px #00e6e6, 0 0 15px #00e6e6, 0 0 20px #00e6e6; }
`;

// Main container for the loading spinner
const SpinnerContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100vh;
    background-color: #1a1a1a;
`;

// Heart shape with pulsing animation and neon glow
const Heart = styled.div`
    position: relative;
    width: 100px;
    height: 100px;
    background-color: #e63946;
    transform: rotate(-45deg);
    animation: ${pulse} 1s infinite;
    box-shadow: 0 0 20px #00e6e6, 0 0 30px #00e6e6, 0 0 40px #00e6e6;

    &::before,
    &::after {
        content: '';
        position: absolute;
        width: 100px;
        height: 100px;
        background-color: #e63946;
        border-radius: 50%;
    }
    
    &::before { top: -50px; left: 0; }
    &::after { top: 0; left: 50px; }
`;

// Pulsing circle effect within the heart
const PulseEffect = styled.div`
    position: absolute;
    top: 50%;
    left: 50%;
    width: 60px;
    height: 60px;
    background-color: rgba(230, 57, 70, 0.5);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    animation: ${pulse} 1s infinite;
    box-shadow: 0 0 20px #00e6e6, 0 0 30px #00e6e6;
`;

// Loading text with animated neon glow effect
const LoadingText = styled.p`
    color: #00e6e6;
    font-size: 1.2rem;
    font-weight: bold;
    margin-top: 20px;
    animation: ${neonGlow} 1.5s infinite;
`;

const LoadingSpinner = () => (
    <SpinnerContainer>
        <Heart>
            <PulseEffect />
        </Heart>
        <LoadingText>Loading...</LoadingText>
    </SpinnerContainer>
);

export default LoadingSpinner;
