// src/pages/ContactPage.js
import React, { useEffect } from 'react';

const ContactPage = () => {
    useEffect(() => {
        const canvas = document.getElementById('animationCanvas');
        const ctx = canvas.getContext('2d');
        let width = window.innerWidth;
        let height = window.innerHeight;
        canvas.width = width;
        canvas.height = height;

        // Create shapes (lines, circles, rectangles, triangles)
        const shapes = Array.from({ length: 30 }, () => {
            const type = Math.floor(Math.random() * 4); // Randomly choose a shape type
            return {
                x: Math.random() * width,
                y: Math.random() * height,
                dx: (Math.random() - 0.5) * 3,
                dy: (Math.random() - 0.5) * 3,
                size: Math.random() * 20 + 10,
                type: type, // 0: line, 1: circle, 2: rectangle, 3: triangle
            };
        });

        // Draw shapes
        function drawShape(shape) {
            ctx.beginPath();
            switch (shape.type) {
                case 0: // Line
                    const endX = shape.x + Math.cos(Math.random() * Math.PI * 2) * shape.size;
                    const endY = shape.y + Math.sin(Math.random() * Math.PI * 2) * shape.size;
                    ctx.moveTo(shape.x, shape.y);
                    ctx.lineTo(endX, endY);
                    ctx.strokeStyle = `hsl(${Math.random() * 360}, 100%, 50%)`; // Bright color
                    ctx.lineWidth = 2;
                    ctx.stroke();
                    break;
                case 1: // Circle
                    ctx.arc(shape.x, shape.y, shape.size, 0, Math.PI * 2);
                    ctx.fillStyle = `hsl(${Math.random() * 360}, 100%, 50%)`; // Bright color
                    ctx.fill();
                    break;
                case 2: // Rectangle
                    ctx.rect(shape.x, shape.y, shape.size, shape.size / 2);
                    ctx.fillStyle = `hsl(${Math.random() * 360}, 100%, 50%)`; // Bright color
                    ctx.fill();
                    break;
                case 3: // Triangle
                    ctx.moveTo(shape.x, shape.y);
                    ctx.lineTo(shape.x - shape.size, shape.y + shape.size);
                    ctx.lineTo(shape.x + shape.size, shape.y + shape.size);
                    ctx.closePath();
                    ctx.fillStyle = `hsl(${Math.random() * 360}, 100%, 50%)`; // Bright color
                    ctx.fill();
                    break;
                default:
                    break;
            }
        }

        // Update and draw shapes
        function animate() {
            ctx.clearRect(0, 0, width, height);
            shapes.forEach((shape) => {
                // Move shapes
                shape.x += shape.dx;
                shape.y += shape.dy;

                // Bounce shapes off edges
                if (shape.x <= 0 || shape.x >= width) shape.dx *= -1;
                if (shape.y <= 0 || shape.y >= height) shape.dy *= -1;

                // Draw shape
                drawShape(shape);

                // Collision detection
                shapes.forEach((otherShape) => {
                    if (otherShape !== shape) {
                        const distX = otherShape.x - shape.x;
                        const distY = otherShape.y - shape.y;
                        const distance = Math.sqrt(distX * distX + distY * distY);

                        if (distance < shape.size + otherShape.size) {
                            shape.dx *= -1;
                            shape.dy *= -1;
                            otherShape.dx *= -1;
                            otherShape.dy *= -1;
                        }
                    }
                });
            });

            requestAnimationFrame(animate);
        }

        animate();

        // Resize canvas on window resize
        window.addEventListener('resize', () => {
            width = window.innerWidth;
            height = window.innerHeight;
            canvas.width = width;
            canvas.height = height;
        });

        return () => {
            window.removeEventListener('resize', () => {
                width = window.innerWidth;
                height = window.innerHeight;
                canvas.width = width;
                canvas.height = height;
            });
        };
    }, []);

    return (
        <div className="contact-page">
            <canvas id="animationCanvas"></canvas>
            <h1>Contact Me</h1>
            <form className="contact-form" action="https://formspree.io/f/xgedqnry" method="POST">
                <label htmlFor="name">Name</label>
                <input type="text" id="name" name="name" required />

                <label htmlFor="email">Email</label>
                <input type="email" id="email" name="email" required />

                <label htmlFor="message">Message</label>
                <textarea id="message" name="message" required></textarea>

                <button type="submit">Send Message</button>
            </form>

            {/* Styles */}
            <style jsx>{`
                :root {
                    --primary-color: #00bcd4;
                    --background-color: #121212;
                    --form-background: rgba(30, 30, 30, 0.9);
                    --text-color: #f1f1f1;
                    --border-color: #555;
                    --accent-color: #ff5722;
                }

                .contact-page {
                    position: relative;
                    padding: 2rem;
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    background: var(--background-color);
                    min-height: 100vh;
                    color: var(--text-color);
                }

                canvas {
                    position: fixed;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    z-index: -1;
                }

                h1 {
                    font-size: 2.5rem;
                    text-align: center;
                    color: var(--primary-color);
                    margin-bottom: 2rem;
                }

                .contact-form {
                    background: var(--form-background);
                    border-radius: 10px;
                    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.5);
                    padding: 2rem;
                    width: 100%;
                    max-width: 500px;
                    display: flex;
                    flex-direction: column;
                    gap: 1.2rem;
                }

                label {
                    font-weight: bold;
                    color: var(--text-color);
                }

                input, textarea {
                    padding: 0.5rem;
                    border: 1px solid var(--border-color);
                    background: #222;
                    color: var(--text-color);
                    border-radius: 4px;
                    transition: border-color 0.3s, box-shadow 0.3s;
                }

                input:focus, textarea:focus {
                    border-color: var(--primary-color);
                    box-shadow: 0 0 8px rgba(0, 188, 212, 0.5);
                }

                button {
                    padding: 0.6rem 1.2rem;
                    background-color: var(--primary-color);
                    color: #fff;
                    border: none;
                    border-radius: 4px;
                    cursor: pointer;
                    transition: background-color 0.3s, transform 0.3s;
                    font-size: 1rem;
                }

                button:hover {
                    background-color: var(--accent-color);
                    transform: scale(1.05);
                }
            `}</style>
        </div>
    );
};

export default ContactPage;
