// src/components/HeroSection.js
import React, { useEffect } from 'react';
import { gsap } from 'gsap';

const HeroSection = () => {
    const styles = {
        hero: {
            height: '80vh', // Adjust height as needed
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            background: 'linear-gradient(135deg, #1a1a1a, #333), url("/background.jpg") center/cover no-repeat',
            color: '#fff',
            textAlign: 'center',
            padding: '2rem',
        },
        title: {
            fontSize: '4rem', // Adjust title size
            marginBottom: '1rem',
        },
        subtitle: {
            fontSize: '1.5rem', // Adjust subtitle size
        },
    };

    return (
        <section style={styles.hero}>
            <h1 style={styles.title}>Welcome to My Portfolio</h1>
            <p style={styles.subtitle}>I'm Udhaya Kumar A, a Computer Science Engineer.</p>
        </section>
    );
};

export default HeroSection;
