import React, { useEffect } from 'react';
import { gsap } from 'gsap';

const HeroSection = () => {
  const styles = {
    hero: {
      position: 'relative',
      height: '100vh',
      width: '100%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      overflow: 'hidden',
      background: 'linear-gradient(135deg, #1c1c1c, #333)',
    },
    content: {
      position: 'relative',
      zIndex: 2,
      color: 'white',
      textAlign: 'center',
      maxWidth: '800px',
      padding: '2rem',
    },
    title: {
      fontSize: '3rem',
      marginBottom: '1rem',
      color: '#ffd700',
      textShadow: '0px 0px 10px rgba(255,215,0,0.5)',
    },
    tagline: {
      fontSize: '1.5rem',
      color: '#ffd700',
      marginBottom: '1.5rem',
    },
    description: {
      fontSize: '1.2rem',
      marginBottom: '2rem',
    },
    buttonContainer: {
      display: 'flex',
      gap: '1rem',
      justifyContent: 'center',
    },
    primaryButton: {
      backgroundColor: '#007bff',
      color: '#fff',
      padding: '0.75rem 1.5rem',
      borderRadius: '5px',
      textDecoration: 'none',
      fontSize: '1.1rem',
    },
    secondaryButton: {
      backgroundColor: '#6c757d',
      color: '#fff',
      padding: '0.75rem 1.5rem',
      borderRadius: '5px',
      textDecoration: 'none',
      fontSize: '1.1rem',
    },
    floatingObjects: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      overflow: 'hidden',
      zIndex: -1,
    },
  };

  useEffect(() => {
    gsap.fromTo('.hero-title', { opacity: 0, y: -50 }, { opacity: 1, y: 0, duration: 1 });
    gsap.fromTo('.hero-tagline', { opacity: 0, y: -30 }, { opacity: 1, y: 0, duration: 0.8 });
    gsap.fromTo('.hero-description', { opacity: 0, y: 30 }, { opacity: 1, y: 0, duration: 0.8 });
    gsap.fromTo('.hero-button', { opacity: 0, scale: 0.9 }, { opacity: 1, scale: 1, duration: 0.4, stagger: 0.2 });
  }, []);

  return (
    <section style={styles.hero}>
      <div style={styles.floatingObjects}>
        <div className="object object-1"></div>
        <div className="object object-2"></div>
        <div className="object object-3"></div>
        <div className="object object-4"></div>
        <div className="object object-5"></div>
      </div>

      <div style={styles.content}>
        <h1 className="hero-title" style={styles.title}>Hello, I'm Udhaya Kumar A</h1>
        <p className="hero-tagline" style={styles.tagline}>Aspiring Public Speaker, AI Enthusiast, and Technology Innovator</p>
        <p className="hero-description" style={styles.description}>
          A Computer Science Engineering student passionate about harnessing the power of AI and technology to solve real-world challenges.
        </p>
        <div style={styles.buttonContainer}>
          <a href="#projects" className="hero-button" style={styles.primaryButton}>
            View Projects
          </a>
          <a href="/path/to/resume.pdf" target="_blank" className="hero-button" style={styles.secondaryButton}>
            Download Resume
          </a>
        </div>
      </div>

      <style>
        {`
          .object {
            position: absolute;
            border-radius: 50%;
            background: rgba(255, 215, 0, 0.2);
            opacity: 0.8;
            animation: floatAnimation 10s linear infinite alternate;
          }
          .object-1 { width: 80px; height: 80px; top: 15%; left: 5%; }
          .object-2 { width: 120px; height: 120px; top: 55%; left: 25%; }
          .object-3 { width: 100px; height: 100px; top: 30%; left: 65%; }
          .object-4 { width: 60px; height: 60px; top: 5%; left: 80%; }
          .object-5 { width: 90px; height: 90px; top: 80%; left: 40%; }

          @keyframes floatAnimation {
            0% { transform: translateY(0) translateX(0); }
            100% { transform: translateY(-20px) translateX(10px); }
          }
        `}
      </style>
    </section>
  );
};

export default HeroSection;
