// src/pages/HomePage.js
import React, { useEffect, useRef } from 'react';
import { gsap } from 'gsap';
import HeroSection from '../components/HeroSection';
import AboutMe from '../components/AboutMe';
import Skills from '../components/Skills';
import Projects from '../components/Projects';
import Experience from '../components/Experience';
import Certifications from '../components/Certifications';
import MicroInteractions from '../components/MicroInteractions';
import KineticTypography from '../components/KineticTypography';

const HomePage = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        gsap.fromTo('.intro-description', { opacity: 0, y: 20 }, { opacity: 1, y: 0, duration: 1, delay: 1 });

        // Canvas animation logic
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        const draw = (width, height) => {
            ctx.clearRect(0, 0, width, height);
            ctx.fillStyle = 'rgba(255, 255, 255, 0.5)';
            ctx.beginPath();
            ctx.arc(Math.random() * width, Math.random() * height, 30, 0, Math.PI * 2, true);
            ctx.fill();
        };

        const animate = () => {
            draw(canvas.width, canvas.height);
            requestAnimationFrame(animate);
        };

        animate();

        // Set canvas dimensions
        const setCanvasSize = () => {
            canvas.width = window.innerWidth;
            canvas.height = window.innerHeight;
        };

        setCanvasSize();
        window.addEventListener('resize', setCanvasSize);

        return () => {
            window.removeEventListener('resize', setCanvasSize);
        };
    }, []);

    return (
        <div className="home-page">
            <canvas
                ref={canvasRef}
                style={{ position: 'absolute', top: 0, left: 0, zIndex: -1 }}
            />
            <HeroSection />
            <section className="introduction">
                <h2 className="title typewriter-text" style={{ color: '#0ff', textShadow: '0 0 10px #0ff, 0 0 20px #0ff' }}>Welcome to My Portfolio!</h2>
                <p className="description intro-description">
                    I'm Udhaya Kumar A, a Computer Science Engineer driven by innovation and a passion for AI, full-stack development, and data science.
                </p>
            </section>
            <Skills />
            <Projects />
            <Experience />
            <Certifications />
            <MicroInteractions />
            <KineticTypography />
            <AboutMe />

            <style jsx>{`
                :root {
                    --text-color: #f1f1f1;
                    --accent-color: #00ffff;
                    --background-gradient: linear-gradient(135deg, #1a1a1a, #333), url("/background.jpg") center/cover no-repeat;
                }

                body {
                    margin: 0;
                    font-family: 'Arial', sans-serif;
                }

                .home-page {
                    color: var(--text-color);
                    background: var(--background-gradient);
                    padding: 2rem;
                }

                .introduction {
                    text-align: center;
                    margin: 4rem 0;
                    padding: 3rem 2rem;
                    background: rgba(255, 255, 255, 0.1);
                    box-shadow: 0 8px 12px rgba(0, 255, 255, 0.3);
                    border-radius: 15px;
                    animation: fadeIn 1.5s ease-in-out;
                }

                .title {
                    font-size: 3rem;
                    color: var(--accent-color);
                    margin-bottom: 1rem;
                    animation: typing 2s steps(40, end), blink 0.5s step-end infinite alternate;
                }

                .description {
                    font-size: 1.2rem;
                    line-height: 1.6;
                    max-width: 800px;
                    margin: 0 auto;
                    color: var(--text-color);
                    opacity: 0;
                    transform: translateY(20px);
                }

                /* Keyframe Animations */
                @keyframes fadeIn {
                    from { opacity: 0; }
                    to { opacity: 1; }
                }

                @keyframes typing {
                    from { width: 0; }
                    to { width: 100%; }
                }

                /* Hover Effects */
                .introduction:hover {
                    filter: brightness(1.2);
                    transition: filter 0.3s ease-in-out;
                }

                /* Media Queries for Responsiveness */
                @media (max-width: 768px) {
                    .title {
                        font-size: 2.5rem;
                    }

                    .description {
                        font-size: 1rem;
                    }
                }
            `}</style>
        </div>
    );
};

export default HomePage;
