// src/components/LoadingSpinner.js
import React from 'react';
import styled, { keyframes } from 'styled-components';

// Keyframes for the neon glow effect
const glow = keyframes`
    0%, 100% { box-shadow: 0 0 10px rgba(0, 230, 230, 0.8), 0 0 20px rgba(0, 230, 230, 0.6); }
    50% { box-shadow: 0 0 15px rgba(0, 230, 230, 1), 0 0 30px rgba(0, 230, 230, 1); }
`;

// Keyframes for inner pulse animation
const pulse = keyframes`
    0%, 100% { transform: scale(1); opacity: 0.7; }
    50% { transform: scale(1.5); opacity: 1; }
`;

// Container for the loading spinner
const SpinnerContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100vh;
    background-color: #1a1a1a; /* Dark background */
`;

// Pulsating circle with neon glow effect
const PulsatingCircle = styled.div`
    width: 100px; /* Size of the pulse */
    height: 100px; /* Size of the pulse */
    background-color: rgba(0, 230, 230, 0.5); /* Neon color */
    border-radius: 50%; /* Circular shape */
    animation: ${pulse} 1.5s infinite; /* Pulse animation */
    box-shadow: ${glow} 2s infinite alternate; /* Glow effect */
`;

// Loading text with neon glow effect
const LoadingText = styled.p`
    color: #00e6e6;
    font-size: 1.2rem;
    font-weight: bold;
    margin-top: 20px;
    text-shadow:
        0 0 5px #00e6e6,
        0 0 10px #00e6e6; /* Neon glow for text */
`;

const LoadingSpinner = () => (
    <SpinnerContainer>
        <PulsatingCircle />
        <LoadingText>Loading...</LoadingText>
    </SpinnerContainer>
);

export default LoadingSpinner;
