// src/pages/ProjectsPage.js
import React from 'react';
import ProjectList from '../components/ProjectList';
import ScrollAnimations from '../components/ScrollAnimations';
import { gsap } from 'gsap';

const ProjectsPage = () => {
    React.useEffect(() => {
        gsap.fromTo('.featuredSection', { opacity: 0, y: 50 }, { opacity: 1, y: 0, duration: 1, delay: 0.5 });
    }, []);

    return (
        <div className="page-container">
            <h1 className="title">My Projects</h1>
            <ScrollAnimations />
            <ProjectList />
            <section className="featured-section featuredSection">
                <h2 className="featured-title">Featured Projects</h2>
                <p className="featured-description">Here are some of the standout projects I’ve worked on:</p>
                <div className="project-grid">
                    {/* Featured project highlights */}
                    <div className="featured-project-card">
                        <h3>AI-Based Atmospheric Model</h3>
                        <p>Integrates real-time satellite data for tracking pollutant dispersion.</p>
                        <a href="#" className="project-link">View Project</a>
                    </div>
                    <div className="featured-project-card">
                        <h3>Multi-Modal Gunshot Detection System</h3>
                        <p>Advanced detection system using AI technologies.</p>
                        <a href="#" className="project-link">View Project</a>
                    </div>
                    <div className="featured-project-card">
                        <h3>Cognitive Function Simulation</h3>
                        <p>Mimics cognitive functions using neural networks.</p>
                        <a href="#" className="project-link">View Project</a>
                    </div>
                </div>
            </section>

            {/* Inline CSS for advanced styles */}
            <style jsx>{`
                :root {
                    --text-color: #f1f1f1;
                    --accent-color: #ffd700;
                    --background-color: #1e1e1e;
                    --card-background: rgba(255, 255, 255, 0.1);
                    --hover-color: rgba(255, 255, 255, 0.15);
                    --shadow-color: rgba(0, 0, 0, 0.2);
                }

                .page-container {
                    padding: 2rem;
                    background-color: var(--background-color);
                    font-family: 'Arial', sans-serif;
                    color: var(--text-color);
                    min-height: 100vh; /* Ensure full height */
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                }

                .title {
                    font-size: 3rem;
                    text-align: center;
                    color: var(--accent-color);
                    text-shadow: 2px 2px 6px rgba(0, 0, 0, 0.3);
                    margin-bottom: 2rem;
                }

                .featured-section {
                    margin-top: 3rem;
                    padding: 2rem;
                    border-radius: 10px;
                    text-align: center;
                    background: var(--card-background);
                    box-shadow: 0 8px 16px var(--shadow-color);
                }

                .featured-title {
                    font-size: 2.5rem;
                    color: var(--accent-color);
                    margin-bottom: 0.8rem;
                }

                .featured-description {
                    font-size: 1.2rem;
                    color: #ddd;
                    margin-bottom: 2rem;
                }

                .project-grid {
                    display: grid;
                    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
                    gap: 1.5rem; /* Space between project cards */
                }

                .featured-project-card {
                    background-color: var(--hover-color);
                    border-radius: 8px;
                    padding: 1rem;
                    box-shadow: 0 4px 8px var(--shadow-color);
                    transition: transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out; /* Smooth transitions */
                }

                .featured-project-card:hover {
                   transform: translateY(-5px); /* Lift effect on hover */
                   box-shadow: 0 12px 24px rgba(0, 0, 0, 0.4); /* Shadow effect on hover */
                }

                .project-link {
                   color: var(--accent-color);
                   text-decoration: underline;
                   transition: color 0.3s; /* Smooth color transition */
                }

                .project-link:hover {
                   color: #fff; /* Change link color on hover */
                }
            `}</style>
        </div>
    );
};

export default ProjectsPage;
