// src/components/MicroInteractions.js
import React, { useState } from 'react';
import { gsap } from 'gsap';

const MicroInteractions = () => {
    const [toggle, setToggle] = useState(false);

    const handleToggle = () => {
        setToggle(!toggle);
        gsap.to(".toggle-button", {
            backgroundColor: toggle ? '#ddd' : '#1e90ff',
            color: toggle ? 'black' : 'white',
            duration: 0.3
        });
    };

    const handleHoverIn = (e) => {
        gsap.to(e.target, {
            scale: 1.1,
            backgroundColor: '#eee',
            duration: 0.2,
        });
    };

    const handleHoverOut = (e) => {
        gsap.to(e.target, {
            scale: 1,
            backgroundColor: '#ddd',
            duration: 0.2,
        });
    };

    return (
        <section style={{
            padding: '2rem',
            textAlign: 'center',
            backgroundColor: '#f4f4f9'
        }}>
            <h2 style={{
                fontSize: '1.8rem',
                color: '#333',
                marginBottom: '1rem'
            }}>Micro Interactions</h2>
            <div style={{ marginBottom: '1rem' }}>
                <button
                    style={{
                        padding: '0.8rem 1.2rem',
                        fontSize: '1rem',
                        backgroundColor: '#ddd',
                        border: '1px solid #bbb',
                        borderRadius: '6px',
                        transition: 'background-color 0.3s',
                        cursor: 'pointer',
                        transformOrigin: 'center'
                    }}
                    onMouseEnter={handleHoverIn}
                    onMouseLeave={handleHoverOut}
                >
                    Hover Over Me!
                </button>
            </div>
            <div>
                <button
                    className="toggle-button"
                    style={{
                        padding: '0.8rem 1.2rem',
                        fontSize: '1rem',
                        backgroundColor: toggle ? '#1e90ff' : '#ddd',
                        color: toggle ? 'white' : 'black',
                        border: '1px solid #bbb',
                        borderRadius: '6px',
                        cursor: 'pointer',
                        transition: 'background-color 0.3s, color 0.3s',
                    }}
                    onClick={handleToggle}
                >
                    {toggle ? 'Toggle ON' : 'Toggle OFF'}
                </button>
            </div>
        </section>
    );
};

export default MicroInteractions;
